import { useQuery, useMutation } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Calendar, Users, Activity, Edit, Play } from "lucide-react";
import { format } from "date-fns";
import JoinSessionDialog from "./JoinSessionDialog";
import { useToast } from "@/hooks/use-toast";
import { queryClient, apiRequest } from "@/lib/queryClient";
import type { Session, Registration } from "@shared/schema";

interface SessionCardProps {
  session: Session;
  onViewDetails?: () => void;
  onEdit?: () => void;
}

export default function SessionCard({
  session,
  onViewDetails,
  onEdit,
}: SessionCardProps) {
  const { toast } = useToast();
  const [, setLocation] = useLocation();

  const { data: registrations = [] } = useQuery<Registration[]>({
    queryKey: ["/api/sessions", session.id, "registrations"],
  });

  const createDrawsMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest("POST", `/api/sessions/${session.id}/draws`);
      return await response.json();
    },
    onSuccess: (data) => {
      queryClient.invalidateQueries({ queryKey: ["/api/sessions", session.id, "matches"] });
      toast({
        title: "Success",
        description: `Generated ${data.matchCount} matches across ${session.numberOfRounds} rounds`,
      });
      // Navigate to session detail page to view draws
      setLocation(`/sessions/${session.id}`);
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to generate draws",
        variant: "destructive",
      });
    },
  });

  const registeredCount = registrations.length;
  const isFull = registeredCount >= session.capacity;
  const status = session.status as "upcoming" | "active" | "completed";

  const statusColors = {
    upcoming: "bg-blue-500/10 text-blue-700 dark:text-blue-400",
    active: "bg-green-500/10 text-green-700 dark:text-green-400",
    completed: "bg-muted text-muted-foreground",
  };

  const statusBorderColors = {
    upcoming: "border-l-blue-500",
    active: "border-l-green-500",
    completed: "border-l-muted",
  };

  return (
    <Card className={`border-l-4 ${statusBorderColors[status]} hover-elevate`}>
      <CardHeader className="flex flex-row items-center justify-between gap-4 space-y-0 pb-2">
        <CardTitle className="text-lg font-semibold">{session.name}</CardTitle>
        <div className="flex items-center gap-2">
          <Badge className={statusColors[status]}>{status}</Badge>
          {onEdit && (
            <Button
              variant="ghost"
              size="icon"
              className="h-8 w-8 shrink-0"
              onClick={onEdit}
              data-testid="button-edit-session"
            >
              <Edit className="h-4 w-4" />
            </Button>
          )}
        </div>
      </CardHeader>
      <CardContent className="space-y-4">
        <div className="flex flex-col gap-2 text-sm">
          <div className="flex items-center gap-2 text-muted-foreground">
            <Calendar className="h-4 w-4" />
            <span>{format(new Date(session.date), "MMM dd, yyyy 'at' h:mm a")}</span>
          </div>
          <div className="flex items-center gap-2 text-muted-foreground">
            <Activity className="h-4 w-4" />
            <span>{session.sessionTypes.join(", ")}</span>
          </div>
          <div className="flex items-center gap-2 text-muted-foreground">
            <Users className="h-4 w-4" />
            <span>
              {registeredCount}/{session.capacity} players â€¢ {session.courtsAvailable} courts
            </span>
          </div>
          {isFull && (
            <Badge className="bg-green-500/10 text-green-700 dark:text-green-400 w-fit">
              Session Full
            </Badge>
          )}
        </div>
        <div className="flex flex-col gap-2">
          {status === "upcoming" && !isFull && (
            <JoinSessionDialog session={session} />
          )}
          {status === "upcoming" && isFull && (
            <Button
              className="w-full"
              variant="default"
              onClick={() => createDrawsMutation.mutate()}
              disabled={createDrawsMutation.isPending}
              data-testid="button-create-draws"
            >
              <Play className="h-4 w-4 mr-2" />
              {createDrawsMutation.isPending ? "Generating..." : "Create Draws"}
            </Button>
          )}
          {onViewDetails && (
            <Button
              className="w-full"
              variant="outline"
              onClick={onViewDetails}
              data-testid="button-view-session"
            >
              View Details
            </Button>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

shared/schema.ts.content:
import { sql } from "drizzle-orm";
import { pgTable, text, varchar, integer, timestamp } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  username: text("username").notNull().unique(),
  password: text("password").notNull(),
});

export const insertUserSchema = createInsertSchema(users).pick({
  username: true,
  password: true,
});

export type InsertUser = z.infer<typeof insertUserSchema>;
export type User = typeof users.$inferSelect;

export const players = pgTable("players", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull(),
  gender: text("gender").notNull(),
  club: text("club"),
  singlesRating: integer("singles_rating"),
  mensDoublesRating: integer("mens_doubles_rating"),
  womensDoublesRating: integer("womens_doubles_rating"),
  mixedDoublesRating: integer("mixed_doubles_rating"),
  preferredCategories: text("preferred_categories").array().notNull(),
  notes: text("notes"),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const insertPlayerSchema = createInsertSchema(players).omit({
  id: true,
  createdAt: true,
});

export type InsertPlayer = z.infer<typeof insertPlayerSchema>;
export type Player = typeof players.$inferSelect;

export const sessions = pgTable("sessions", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull(),
  date: timestamp("date").notNull(),
  sessionTypes: text("session_types").array().notNull(),
  capacity: integer("capacity").notNull(),
  courtsAvailable: integer("courts_available").notNull(),
  numberOfRounds: integer("number_of_rounds").notNull(),
  maxSkillGap: integer("max_skill_gap"),
  minGamesPerPlayer: integer("min_games_per_player"),
  status: text("status").notNull(),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const insertSessionSchema = createInsertSchema(sessions).omit({
  id: true,
  createdAt: true,
});

export type InsertSession = z.infer<typeof insertSessionSchema>;
export type Session = typeof sessions.$inferSelect;

export const registrations = pgTable("registrations", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  sessionId: varchar("session_id").notNull(),
  playerId: varchar("player_id").notNull(),
  selectedEvents: text("selected_events").array().notNull(),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const insertRegistrationSchema = createInsertSchema(registrations).omit({
  id: true,
  createdAt: true,
});

export type InsertRegistration = z.infer<typeof insertRegistrationSchema>;
export type Registration = typeof registrations.$inferSelect;

export const matches = pgTable("matches", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  sessionId: varchar("session_id").notNull(),
  eventType: text("event_type").notNull().default('singles'),
  courtNumber: integer("court_number").notNull(),
  roundNumber: integer("round_number").notNull(),
  team1Player1Id: varchar("team1_player1_id").notNull(),
  team1Player2Id: varchar("team1_player2_id"),
  team2Player1Id: varchar("team2_player1_id").notNull(),
  team2Player2Id: varchar("team2_player2_id"),
  team1Set1: integer("team1_set1"),
  team1Set2: integer("team1_set2"),
  team1Set3: integer("team1_set3"),
  team2Set1: integer("team2_set1"),
  team2Set2: integer("team2_set2"),
  team2Set3: integer("team2_set3"),
  status: text("status").notNull(),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const insertMatchSchema = createInsertSchema(matches).omit({
  id: true,
  createdAt: true,
});

export type InsertMatch = z.infer<typeof insertMatchSchema>;
export type Match = typeof matches.$inferSelect;

export const ratingHistories = pgTable("rating_histories", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  playerId: varchar("player_id").notNull(),
  eventType: text("event_type").notNull(),
  oldRating: integer("old_rating").notNull(),
  newRating: integer("new_rating").notNull(),
  ratingChange: integer("rating_change").notNull(),
  matchId: varchar("match_id").notNull(),
  opponentIds: text("opponent_ids").array().notNull(),
  result: text("result").notNull(), // "win", "loss"
  expectedOutcome: text("expected_outcome").notNull(), // "win", "loss", "even"
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const insertRatingHistorySchema = createInsertSchema(ratingHistories).omit({
  id: true,
  createdAt: true,
});

export type InsertRatingHistory = z.infer<typeof insertRatingHistorySchema>;
export type RatingHistory = typeof ratingHistories.$inferSelect;

