  --shadow-sm: 0px 2px 4px -1px hsl(220 20% 5% / 0.45), 0px 1px 2px -1px hsl(220 20% 5% / 0.60);
  --shadow: 0px 4px 6px -1px hsl(220 20% 5% / 0.50), 0px 2px 4px -2px hsl(220 20% 5% / 0.40);
  --shadow-md: 0px 6px 12px -2px hsl(220 20% 5% / 0.55), 0px 3px 7px -3px hsl(220 20% 5% / 0.45);
  --shadow-lg: 0px 10px 20px -4px hsl(220 20% 5% / 0.60), 0px 4px 8px -4px hsl(220 20% 5% / 0.50);
  --shadow-xl: 0px 20px 30px -6px hsl(220 20% 5% / 0.70), 0px 8px 16px -6px hsl(220 20% 5% / 0.55);
  --shadow-2xl: 0px 25px 50px -12px hsl(220 20% 5% / 0.80);

  /* Fallback for older browsers */
  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --sidebar-accent-border: hsl(var(--sidebar-accent));
